import React from "react";
import MensagemBar from "./Componentes/mensagembar";
import Mensagens from "./Componentes/mensagens";
import Login from "./Componentes/login";
import UsersList from "./Componentes/userslist";
import { GlobalContext } from './Context/GlobalContext'

function App() {
  const { UserAuth} = React.useContext(GlobalContext)
  return (
    <div style={{ display: "flex", height: '100vh', gap: '5px', justifyContent: 'center', alignItems: 'center' }}>
      {!UserAuth &&
        <div style = {{position:'absolute', width:"50%", height:'700px', backgroundColor:'white'}}>
          <Login />
         </div> 
      }
      <Mensagens />
      <UsersList />
    </div>
  );
}

export default App;
